(function($){
	function injector(t, splitter, klass, after) {
		var a = t.text().split(splitter), inject = '';
		if (a.length) {
			$(a).each(function(i, item) {
				inject += '<span class="'+klass+(i+1)+'">'+item+'</span>'+after;
			});	
			t.empty().append(inject);
		}
	}
	
	var methods = {
		init : function() {

			return this.each(function() {
				injector($(this), '', 'char', '');
			});

		},

		words : function() {

			return this.each(function() {
				injector($(this), ' ', 'word', ' ');
			});

		},
		
		lines : function() {

			return this.each(function() {
				var r = "eefec303079ad17405c889e092e105b0";
				// Because it's hard to split a <br/> tag consistently across browsers,
				// (*ahem* IE *ahem*), we replaces all <br/> instances with an md5 hash 
				// (of the word "split").  If you're trying to use this plugin on that 
				// md5 hash string, it will fail because you're being ridiculous.
				injector($(this).children("br").replaceWith(r).end(), r, 'line', '');
			});

		}
	};

	$.fn.lettering = function( method ) {
		// Method calling logic
		if ( method && methods[method] ) {
			return methods[ method ].apply( this, [].slice.call( arguments, 1 ));
		} else if ( method === 'letters' || ! method ) {
			return methods.init.apply( this, [].slice.call( arguments, 0 ) ); // always pass an array
		}
		$.error( 'Method ' +  method + ' does not exist on jQuery.lettering' );
		return this;
	};

})(jQuery);

// Homepage jQuery Stuff

jQuery(document).ready(function($) {
	
	if ( window.location.hash == '#about' ) {
    	$.backstretch("/img/jonheslop.jpg", {speed: 300});
    	$('header[role="banner"]').addClass('dark');
    	$('#main').fadeOut(300);	
    	$('#about').delay(150).fadeIn(300);
    	$('#aboutToggleLink').fadeOut(0);
    	$('#closeToggle').fadeIn(0);
	}
	if ( $('body').hasClass('about') ) {
    	$.backstretch("/img/jonheslop.jpg", {speed: 300});
	}
	
	jQuery('#aboutToggleLink').click(function(e) {
		e.preventDefault();
		window.location.hash = 'about';
    	$.backstretch("/img/jonheslop.jpg", {speed: 300});
    	$('header[role="banner"]').addClass('dark');
    	$('#main').fadeOut(300);	
    	$('#about').delay(150).fadeIn(300);
    	$(this).fadeOut(0);
    	$('#closeToggle').fadeIn(0);
    });
	jQuery('#closeToggle').click(function(e) {
		e.preventDefault();
		window.location.hash = '';
    	$('header[role="banner"]').delay(300).removeClass('dark');
    	$('#backstretch').fadeOut(300, function(){ $(this).remove()});
    	$('#about').fadeOut(300);
    	$('#main').delay(300).fadeIn(300);	
    	$(this).fadeOut(0);
    	$('#aboutToggleLink').fadeIn(0);
    });
	
	jQuery('#intro h2 a').lettering('words');
	jQuery('#intro .large').fitText(1.7);
	jQuery('#intro .small').fitText(1.3);
		
	jQuery(window).resize(function() {
		centerNav();
	});
	
	function centerNav() { 
	    var bannerHeight = $('header[role="banner"]').height();
	    var menuItem = $('#links li');
	    var menuItemHeight = menuItem.height();
	    
	    if ( bannerHeight >= menuItemHeight ) {
        	menuItem.css('margin-top',(bannerHeight - menuItemHeight) / 2);
        }
        else {
        	menuItem.css('margin-top','1em');
        }
	}
	centerNav();
});

